import React, { useState } from 'react';
import Slider from 'react-slick';
import Car from '../assets/car1.jpg';
var settings = {
	dots: true,
	infinite: true,
	speed: 500,
	slidesToShow: 2,
	slidesToScroll: 1,
};
const MainSection = () => {
	const [option, setOption] = useState('1');

	return (
		<div className="w-full">
			<header className="container flex flex-col  justify-center items-start md:max-w-[1480px] m-auto max-w-[600px] ">
				<p className="text-button text-xl font-roboto-condensed">
					Prezentacja firmy
				</p>
				<h1 className="text-5xl font-bebas-neue">
					ZOBACZ NASZA GALERIE ZDJEC
				</h1>
				<div className="flex gap-10 pt-10 font-bold">
					<button
						onClick={() => setOption('1')}
						className={
							option === '1'
								? 'text-button border-b-2 border-button '
								: 'text-black border-b-2 border-primary'
						}
					>
						Samochody osobowe
					</button>
					<button
						onClick={() => setOption('2')}
						className={
							option === '2'
								? 'text-button border-b-2 border-button'
								: 'text-black border-b-2 border-primary'
						}
					>
						Samochody dostawcze
					</button>
				</div>
			</header>
			<section className="w-full py-10 "></section>
		</div>
	);
};

export default MainSection;
