import React from 'react';
import Modal from 'react-modal';

const Footer = () => {
	const [modalIsOpen, setIsOpen] = React.useState(false);

	function openModal() {
		setIsOpen(true);
	}

	function closeModal() {
		setIsOpen(false);
	}
	const customStyles = {
		content: {
			top: '50%',
			left: '50%',
			right: 'auto',
			bottom: 'auto',
			marginRight: '-50%',
			transform: 'translate(-50%, -50%)',
		},
	};
	return (
		<div className="w-full bg-secondary border-t-2">
			<Modal
				isOpen={modalIsOpen}
				onRequestClose={closeModal}
				style={customStyles}
				contentLabel="Example Modal"
			>
				<h1>Polityka prywatności</h1>
				<p>

				</p>
			</Modal>
			<div className="container flex flex-row py-10 px-4 lg:flex-row  justify-between items-center m-auto  text-white text-sm lg:max-w-[1440px]">
				<span>Cars Spot</span>
				<button className="underline" onClick={openModal}>
					Polityka prywatności
				</button>
			</div>
		</div>
	);
};

export default Footer;
