import React, { useState } from 'react';
import Slider from 'react-slick';
import Car from '../assets/car4.jpg';
import Car2 from '../assets/car5.jpg';
import Car3 from '../assets/car6.jpg';
import Car4 from '../assets/car_dost1.jpeg';
import Car5 from '../assets/car_dost2.jpg';
import Car6 from '../assets/car_dost3.jpg';
import { mainSectionText } from '../constants';
var settings = {
	dots: true,
	infinite: true,
	speed: 500,
	slidesToShow: 2,
	slidesToScroll: 1,
	autoplaySpeed: 2000,
	autoplay: true,
	arrows: false,
	responsive: [
		{
			breakpoint: 1080,
			settings: {
				dots: true,
				infinite: true,
				speed: 500,
				slidesToShow: 1,
				slidesToScroll: 1,
				autoplaySpeed: 2000,
				autoplay: true,
				arrows: false,
			},
		},
	],
};
const MainSection = () => {
	const [option, setOption] = useState('1');

	return (
		<div className="w-full" id="mainSection">
			<header className="container flex flex-col  justify-center items-start m-auto px-4  lg:max-w-[1440px]">
				<p className="text-button text-xl font-roboto-condensed">
					{mainSectionText}
				</p>
				<h1 className="text-5xl pt-1 font-bebas-neue">
					ZOBACZ NASZA GALERIE ZDJEC
				</h1>
				<div className="flex gap-10 pt-10 font-bold">
					<button
						onClick={() => setOption('1')}
						className={
							option === '1'
								? 'text-button border-b-2 border-button '
								: 'text-black border-b-2 border-primary'
						}
					>
						Samochody osobowe
					</button>
					<button
						onClick={() => setOption('2')}
						className={
							option === '2'
								? 'text-button border-b-2 border-button'
								: 'text-black border-b-2 border-primary'
						}
					>
						Samochody dostawcze
					</button>
				</div>
			</header>
			<section className="container mx-auto py-10 px-4 lg:max-w-[1440px]">
				<div className="">
					{option === '1' && (
						<div className="w-full ">
							<Slider {...settings} className="text-center">
								<img src={Car} className="w-full " alt="car1" />
								<img
									src={Car2}
									className="w-full "
									alt="car1"
								/>
								<img
									src={Car3}
									className="w-full "
									alt="car1"
								/>
							</Slider>
						</div>
					)}
					{option === '2' && (
						<div className="w-full">
							<Slider {...settings} className="text-center">
								<img
									src={Car4}
									className="max-w-full "
									alt="car1"
								/>
								<img
									src={Car5}
									className="max-w-full"
									alt="car1"
								/>
								<img
									src={Car6}
									className="max-w-full"
									alt="car1"
								/>
							</Slider>
						</div>
					)}
				</div>
			</section>
		</div>
	);
};

export default MainSection;
