import React from 'react';
import Modal from 'react-modal';

const Footer = () => {
	const [modalIsOpen, setIsOpen] = React.useState(false);

	function openModal() {
		setIsOpen(true);
	}

	function closeModal() {
		setIsOpen(false);
	}
	const customStyles = {
		content: {
			top: '50%',
			left: '50%',
			right: 'auto',
			bottom: 'auto',
			width: 500,

			marginRight: '-50%',
			transform: 'translate(-50%, -50%)',

		},
	};
	return (
		<div className="w-full bg-secondary border-t-2 ">
			<Modal
				isOpen={modalIsOpen}
				onRequestClose={closeModal}
				style={customStyles}
				contentLabel="Example Modal"
			>
				<h1>Polityka prywatności</h1>
				<p>
					Lorem ipsum dolor, sit amet consectetur adipisicing elit.
					Consectetur, necessitatibus similique natus, possimus cum
					mollitia, temporibus nihil tenetur beatae quod eum dolorem.
					Culpa eveniet aliquam voluptatibus neque voluptatem ut et
					repudiandae vitae iusto fuga eius, voluptates pariatur.
					Voluptate eius fugit voluptatem sint, ea voluptas obcaecati
					voluptatum possimus veritatis. Harum laudantium dolorum
					labore vitae unde nostrum, est eum corrupti, repellat
					perspiciatis fugit suscipit? Cupiditate delectus sunt,
					similique tempora impedit id. Mollitia, atque ducimus? Odio
					eveniet officiis non, ratione hic culpa vitae.
				</p>
			</Modal>
			<div className="container flex flex-row py-10 px-4 lg:flex-row  justify-between items-center m-auto  text-white text-sm lg:max-w-[1440px]">
				<span>Cars Spot</span>
				<button className="underline" onClick={openModal}>
					Polityka prywatności
				</button>
			</div>
		</div>
	);
};

export default Footer;
