import React from 'react';
import Modal from 'react-modal';

const Footer = () => {
	const [modalIsOpen, setIsOpen] = React.useState(false);

	function openModal() {
		setIsOpen(true);
	}

	function closeModal() {
		setIsOpen(false);
	}
	const customStyles = {
		content: {
			top: '50%',
			left: '50%',
			right: 'auto',
			bottom: 'auto',
			marginRight: '-50%',
			transform: 'translate(-50%, -50%)',
		},
	};
	return (
		<div className="w-full bg-secondary border-t-2">
			<Modal
				isOpen={modalIsOpen}
				onRequestClose={closeModal}
				style={customStyles}
				contentLabel="Example Modal"
			>
				Polityka prywatności określa, jak zbierane, przetwarzane i
				przechowywane są dane osobowe Użytkowników niezbędne do
				świadczenia usług drogą elektroniczną za pośrednictwem serwisu
				internetowego [adres strony] (dalej: Serwis). Serwis zbiera
				wyłącznie dane osobowe niezbędne do świadczenia i rozwoju usług
				w nim oferowanych. Dane osobowe zbierane za pośrednictwem
				Serwisu są przetwarzane zgodnie z Rozporządzeniem Parlamentu
				Europejskiego i Rady (UE) 2016/679 z dnia 27 kwietnia 2016 r. w
				sprawie ochrony osób fizycznych w związku z przetwarzaniem
				danych osobowych i w sprawie swobodnego przepływu takich danych
				oraz uchylenia dyrektywy 95/46/WE (ogólne rozporządzenie o
				ochronie danych, dalej RODO) oraz ustawą o ochronie danych
				osobowych z dnia 10 maja 2018 r.
			</Modal>
			<div className="container flex flex-row py-10 px-4 lg:flex-row  justify-between items-center m-auto  text-white text-sm lg:max-w-[1440px]">
				<span>Cars Spot</span>
				<button className="underline" onClick={openModal}>
					Polityka prywatności
				</button>
			</div>
		</div>
	);
};

export default Footer;
